T = csvread('../data/DOE_p30_t99.csv', 1, 0);% temperature,pressure,hconv,dP_over_l,cp_mol,rho,Re,friction_factort = T(:, 1);p = T(:, 2);h = T(:, 3);dp = T(:, 4);cp = T(:, 5);rho = T(:, 6);re = T(:, 7);f = T(:, 8);figure();plot3(t, p, h, '.');xlabel('Temperature');ylabel('Pressure');zlabel('hconv');figure();plot3(cp, p, h, '.');xlabel('Cp');ylabel('Pressure');zlabel('hconv');figure();plot3(cp, rho, h, '.');xlabel('Cp');ylabel('Density');zlabel('hconv');figure();plot3(t, p, dp, '.');xlabel('Temperature');ylabel('Pressure');zlabel('dP');figure();plot3(rho, p, dp, '.');xlabel('Rho');ylabel('Pressure');zlabel('dP');figure();plot3(t, p, re, '.');xlabel('Temperature');ylabel('Pressure');zlabel('Re');figure();plot3(t, p, rho, '.');xlabel('Temperature');ylabel('Pressure');zlabel('Density');figure();plot3(t, p, f, '.');xlabel('Temperature');ylabel('Pressure');zlabel('Friction Factor');